mapper.select:
select no, title, content, writer, regDate, DATE_FORMAT(regDate, '%Y-%m-%d') as 'date', viewCnt
 from ec_board  where delYn = 'N' 
 order BY no DESC limit #{rowStart},#{perPageNum}

select no, title, content, writer, regDate, DATE_FORMAT(regDate, '%Y-%m-%d') as 'date', viewCnt
 from ec_board  where delYn = 'N' 
 order BY  DESC limit #{rowStart},#{perPageNum}

mapper.update:
	update ec_board 
					set delYn = 'Y'
             where  no = #{no}
